vec4  fC ( in vec2 fragCoord )
{

    vec2 uv =- fragCoord/iResolution.y;
    uv.x += .5;

uv=fract(vec2(uv.x/uv.y,1./uv.y+iTime*1.*sign(uv.y))*.5); // 3D projection, tiling and and forward movement
    //from https://www.shadertoy.com/view/tllfzB

    vec4 ok= texture(iChannel0,uv);
    return ok;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
    float A = 8.,  // Change A to define the level of anti-aliasing (1 to 16) ... higher numbers are REALLY slow!
          s = 1./A, x, y;

    for (x=-.5; x<.5; x+=s) for (y=-.5; y<.5; y+=s) fragColor += min ( fC(vec2(x,y)+fragCoord), 1.0);

	fragColor /= A*A;
}
