const vec3 W = vec3(0.21, 0.91, 0.57);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    vec4 buff = textureLod(iChannel0, uv, 0.);
    vec4 mipm = textureLod(iChannel0, uv, 6.);

	fragColor = buff;

    // bloom
    vec4 bm = dot(mipm.rgb, W) > dot(buff.rgb, W) ? mipm : buff;      
    fragColor += pow(bm * dot(bm.rgb, W), vec4(1.3)) * .6;

    // contrast
    fragColor = pow(fragColor, vec4(1.22));
}

#define xor(a,b) a * (1.0 - b) + b*(1.0 - a)
#define hue(h) clamp( abs( fract(h + vec4(3,2,1,0)/3.) * 6. - 3.) -1. , 0., 1.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;  

    uv.y+=2.0;    
    uv*=.75;    

    float SIZE = 2.5;           
    float t = iTime*.1;

    float m = 0.;
    for(float i=-1.;i<=1.;i+=2.){

        vec2 iuv = uv;
        iuv.x += i;
        float d = fract(length(iuv) * SIZE - t);                
        float mi = smoothstep(0.5, 0.0, abs(d - .5 ));

        m = xor(m,mi);
    }

    float mask = abs(fract(m*5. - iTime) - .5)*5.;

    float l = length(uv)*2.5;

    vec3 col = hue(m + l).rgb;    

    col *= mask;

    fragColor = vec4(col,1.0);
}
