float rand(vec2 co)
{
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float maze(vec2 uv,float s)
{
    uv = floor(uv*s);
    vec2 ev = 1.0-mod(uv,2.0);
    float v = ev.x*ev.y;
    vec2 d=mod(uv,2.0);
    float n = step(rand(uv-d), 0.5);
    d*=vec2(1.0-n,n)*(1.0-d.x*d.y);
    float m=(d.x+d.y);
    return (m*(1.0-v)+v)*(1.0-step(0.8,rand(floor(uv/2.0))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(maze(fragCoord/iResolution.xy, 75.));
}
