int colorCalc(vec2 uv,vec2 circle1)
{
        float d = (mod(distance (uv,circle1),0.04))*25.0;
    if(d>0.5)
    {
        return 0;
    }
    else
    {
        return 1;
    }
}

float interDistance(vec2 a, vec2 b)
{
     return distance(a,b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

       // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;


    vec2 circle1 = vec2(0.8+sin(iTime*1.1)/1.5,   0.5 + sin(iTime)/2.2);
    vec2 circle2 = vec2(1.2+sin(iTime*0.9)/1.5,   0.5 + sin(iTime*0.8)/2.2);

    int c1 = colorCalc(uv,circle1);
    int c2 = colorCalc(uv,circle2);

 	 int c =  c1+c2;
    if (c>=1)
    {
	    fragColor = vec4(1,1,0,1);

    }else
    {
	    fragColor = vec4(0,0,1,1);

    }

}
