float f(inout vec2 p, mat2 m, float t) {
    p.x += sin(t);
    p.y += cos(t);
    float res = length(fract(p *= m * .1) - .5);
    return res;
}

float cell(vec2 p, float t) {

    mat2 m =  mat2(7., -6., 5., 7.);


    // Very cheap wrappable cellular tiles.
    return min(min(f(p,m,t), f(p,m,t)), f(p,m,t));
}

vec3 colouredCell( vec2 p, float t){
    float a1 = cell(p,t)*2.0;

    vec3 base = vec3(0.1, 0.5, 0.75);
    vec3 high = vec3(0.7, 0.9, 0.9);

    return mix(base, high, pow(a1, 5.0));

}


void mainImage(out vec4 o, vec2 p){
    p = p / iResolution.xy*10.;
    float t = iTime / 3.;

    o = vec4(colouredCell(p, t), 1.0);

}
