void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy ) /iResolution.y;

	float angle = iTime;

    vec2 p = vec2( cos( angle ) , sin(angle ) ) * 0.4f;
    vec2 desort = uv - p;
    float d = length( desort );

    float m = ( 0.07f , 0.0f , d );

    m *= 10.0f;


    // Output to screen
    fragColor = vec4(1.0 - m , 1.0 - m , 1.0 - m, 1.0);
}
