const int count = 13;
const float pi = 3.14159265358979;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float padding = 220.0 + cos(iTime) * 20.0;
    float size = 50.0 + sin(iTime) * 10.0;

    vec2 p = (fragCoord - iResolution.xy * 0.5) / 4.0;
    p = p * Rotate(pi / float(count) * 0.5);

    float sum = 0.0;

    for (int i = 0; i < count; i++) {
        float ratio = float(i) / float(count);
        float dir = (p * Rotate(pi * ratio)).x + ratio * (i % 2 == 0 ? 1.0 : -1.0) * 2.0 * iTime;

    	sum = sum + sin(dir);   
    }

    fragColor = vec4(0, sum > 0.0 ? 0.4 : 0.2, sum, 1);   
}


mat2 Rotate(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2(
        vec2(c, -s),
        vec2(s, c)
    );
}
