// 1 = change the colors over time, 
// 0 = change them by dragging your mouse along the screen
#define AUTOHSV 1
#define AUTOHSV_SPEED .15

// Basic HSV/RGB conversion methods by iq: https://www.shadertoy.com/view/lsS3Wc
// The MIT License (Copyright Â© 2014 Inigo Quilez)
const float eps = 0.0000001; vec3 hsv2rgb( in vec3 c ) { vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 ); return c.z * mix( vec3(1.0), rgb, c.y); } vec3 rgb2hsv( in vec3 c) { vec4 k = vec4(0.0, -1.0/3.0, 2.0/3.0, -1.0); vec4 p = mix(vec4(c.zy, k.wz), vec4(c.yz, k.xy), (c.z<c.y) ? 1.0 : 0.0); vec4 q = mix(vec4(p.xyw, c.x), vec4(c.x, p.yzx), (p.x<c.x) ? 1.0 : 0.0); float d = q.x - min(q.w, q.y); return vec3(abs(q.z + (q.w - q.y) / (6.0*d+eps)), d / (q.x+eps), q.x); }



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel/mouse coordinates from 0 to 1
    vec2 uv = fragCoord / iResolution.xy;
    vec2 ms = iMouse.xy / iResolution.xy;

    vec3 srcCol = texture(iChannel0, uv).rgb;


    float hsvMod;
    #if (AUTOHSV)
    hsvMod = iTime;
    #else // manual hsv
    hsvMod = ms.x;
    #endif


    vec3 srcHsv = rgb2hsv(srcCol);
    srcHsv.r += hsvMod * AUTOHSV_SPEED;
    vec3 rgbOut = hsv2rgb(srcHsv);

    fragColor = vec4(rgbOut,1.0);
}
