float unitMirror(in float x)
{
    float f = fract(x / 2.) * 2.;
    if (f > 1.) return 2. - f;
    return f;
}


vec2 unitMirror(in vec2 p)
{
    vec2 q;
    q.x = unitMirror(p.x);
    q.y = unitMirror(p.y);
    return q;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // map coordinates
    uv = unitMirror(uv * 3.0);


    // Output to screen
    fragColor = texture(iChannel0,uv);
}
