#define PI 3.14159265359

float smootherstep(float edge0, float edge1, float x) {
  // Scale, and clamp x to 0..1 range
  x = clamp((x - edge0) / (edge1 - edge0), 0.0, 1.0);
  // Evaluate polynomial
  return x * x * x * (x * (x * 6. - 15.) + 10.);
}

float softsquare(float x) {
    float x2 = abs(2. * fract(x) - 1.);
    return smootherstep(0.1, 0.9, x2);
}

vec3 gradient(float x, float y) {
    const float _2PI3 = 2.094395102393193;
    float g1 = softsquare(x);
    float g2 = softsquare(x - 0.3);
    vec3 blend = (vec3(sin(y), sin(y + _2PI3), sin(y - _2PI3)) + 1.) * 0.5;
    return blend * g2 + (1. - blend) * g1;
}

float spikes(float x) {
    x = 1. - abs(sin(x));
    return x * x;
}

// used for zoom
float time2(float x) {
    return
    fract(x) +
    smootherstep(0.8, 0.97, fract(x)) +
    smootherstep(0.1, 0.03, fract(x))
    ;
}

// used for hue rot.
float time3(float x) {
    return
    smootherstep(0.8, 0.97, fract(x)) +
    smootherstep(0.1, 0.03, fract(x))
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float time = iTime;
    //time = floor(iTime * 60.) / 60.;
    time = fract(time / 8.);

    float hue = time * 2. * PI;
    float flash = time3(time);

    // Normalized pixel coordinates (from 0 to 1)
    float scale = distance(iResolution.x, iResolution.y);
    vec2 uv = fragCoord / scale;
	uv -= vec2(iResolution.x / scale, iResolution.y / scale) / 2.;
    uv *= 2.0;

    float dist = log(uv.x*uv.x+uv.y*uv.y + 0.05) * 1.1;
    float angle = atan(uv.y, uv.x);
    float spokes = 8.5;

    vec3 color = gradient(
        dist +
        (0.3 + 0.1 * time3(time)) * spikes(
            angle * spokes
            + sin(time * 2. * PI) * 0.2
        ) +
        -12. * time2(time - 0.02)
    , hue + 30. * flash + angle);

    // Output to screen
    fragColor = vec4(
        color,
        1
    );
}
