void mainImage( out vec4 O, in vec2 U )
{
    O=vec4(0);//Required on Safari
    vec3 u=normalize(vec3(2.*U-iResolution.xy,iResolution.y))*55.*sin(iTime*.2);
    for(int i=0;i<7;i++){
        u=1.1+cos(u+iTime*.2);
        u.x+=cos(u.z*.1);
        O-=cos(3.*dot(u.xy,u.xy)+vec4(.3,.1,0,0));
    }
}
