void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;
    fragColor = vec4(col,1.0);
}

#define MAX_D 200.
#define denoise_amount .95

vec3 R3(vec3 p, float t){
    return textureLod(iChannel0, p*113.395 + t*vec3(38.15, 85.41, 95.), 0.).rgb*2.-1.;
}


float dist(vec3 p){
    float sphere = distance(p, vec3(0, 0, 7)) -1.;
    float sphere2 = distance(p, vec3(.9, .4, 6.2)) - .5;
    float plane = p.y+1.;
    return min(plane,min(sphere,sphere2));
}
float march(vec3 ro, vec3 rd){
    float t = 0.;
    for(int i=0; i<200; i++){
        vec3 p = ro + rd*t;
        float d = dist(p);
        if(d<.001 || t > MAX_D)break;
        t += d;
    }
    return t;
}
vec3 normal(vec3 p){
    vec2 eps = vec2(.001, 0);
    return (vec3(
        dist(p + eps.xyy),
        dist(p + eps.yxy),
        dist(p + eps.yyx))
        - dist(p))/eps.x;
}



vec3 material(vec3 p){
    return p.y > - .99 && p.z < 20. ? vec3(.9) : vec3(.9, .8, .8);
}

vec3 sky(vec3 d){
    float v = 1.-d.y;
    return vec3(v, v, 1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 color = vec3(0);

    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.x;

    vec3 ro = vec3(0);
    vec3 rd = normalize(vec3(uv, 1.));

    float t = march(ro, rd);
    vec3 p = ro + rd*t;
    vec3 n = normal(p);

    if(t > MAX_D){
        color = sky(rd);
    }else{
        color = material(p);

        vec3 rro = p + n*.001;
        vec3 rrd = normalize(n + .98*R3(p, iTime));
        vec3 prev_n = n;

        for(int i=0; i<10; i++){
        	float rt = march(rro, rrd);
        	vec3 rp = rro + rrd*rt;
        	vec3 rn = normal(rp);

            //material color
            vec3 mcol = material(rp);

            //simple
            color *= mcol;

            //hit the sky?
            if(rt > MAX_D){
                //color *= sky(rrd);
                break;
            }

        	//reinit
        	rro = rp + rn*.001;
        	rrd = normalize(rn + .98*R3(rp, iTime));
            prev_n = rn;
        }
    }

    color = (1.-denoise_amount)*color + denoise_amount*texture(iChannel1, fragCoord.xy/iResolution.xy).rgb;

    fragColor = vec4(color, 1.);
}
