void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize pixel coordinates
    vec2 uv = (fragCoord-0. * iResolution.xy) / iResolution.xy; // 0 <> 1

    uv -= 0.5; // Remap UV // -0.5 <> 0.5
    uv.x *= iResolution.x / iResolution.y; // Fix Aspect Ratio

    float r = 0.3; // Radius
    float er = 1.5/iResolution.y; // Radius with scaleable AA [FabriceNeyret2]
    float d = length(uv); // Distance calcuation
    float c = smoothstep(r, r-er, d); // Set colours to distance values


    //if(d < r) c = 1.; else c = 0.;

    fragColor = vec4(vec3(c), 1.0);   
}
