//tutorial https://www.youtube.com/watch?v=cQXAbndD5CQ

float Xor(float a, float b){
    return a*(1.-b)+b*(1.-a);
    }

mat2 Rot(float a){
    float c = cos(a);
    float s = sin(a);
    return mat2(c,-s,s,c);

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float t = iTime*2.;

    vec3 col =vec3(0);

    uv*=18.;
    uv*=Rot(3.14+t/10.);

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float m =0.;
    id*=1.5*Rot(3.14/2.+t);

    for (float y=-1.;y<=1.;y++){
        for (float x=-1.;x<=1.;x++){
        vec2 offs = vec2(x,y);
        float d = length(gv-offs);
        float dist =length(id+offs)*abs(sin(t/5.)*1.5);

        float r = mix(.1,2.5,cos(dist-t)*.5+.5);
        m +=Xor(m,smoothstep(r,r*.1,d));

        }
    }

    col+=1.-m;
    fragColor = vec4(col,1.0);
}
