/*
*算法来自：https://blog.csdn.net/tjw02241035621611/article/details/80045381
*algorithm from https://blog.csdn.net/tjw02241035621611/article/details/80045381
*/

#define HASHSCALE1 .1031
#define PI 3.14159265

vec3 _Color = vec3(1.0,0.3,0.0);
float _GridSize = 30.;
float _RotSpd = 0.5;
float _YSpd = 0.7;

float Hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 acc = vec3(0.0,0.0,0.0);

    float rotDeg = 3.*_RotSpd * iTime;
    float yOffset = 4.*_YSpd* iTime;

    vec2 coord = uv*_GridSize - vec2(0.,yOffset);//整体沿y轴上升
    if (abs(mod(coord.y,2.0))<1.0){
        coord.x += 0.5;
    }
    vec2 gridIndex = vec2(floor(coord));
    float rnd = Hash12(gridIndex);//根据ID 获取hash值
    // 弥补y轴上升的逆差 获取原来的y值 
    // 同时因为gridIndex = floor(coord) 的原因  会让tempY值在锁定固定的grid的同时越来越大;
    float tempY = gridIndex.y + yOffset ;
    float life = min(10.0*(1.0-min((tempY)/(24.0-20.0*rnd),1.0)),1.0);
    if (life>0.0 ) {
        float size = 0.08*rnd;//让大小随机化
        float deg = 999.0*rnd*2.0*PI + rotDeg*(0.5+0.5*rnd);//添加旋转随机化
        vec2 rotate = vec2(sin(deg),cos(deg));//单位圆旋转偏移
        float radius =  0.5-size*0.2;
        vec2 cirOffset = radius*rotate;//
        vec2 part = fract(coord-cirOffset) - 0.5 ;//让格子自己旋转起来 位置变 方向不变
        float len = length(part);
        float sparksGray = max(0.0,1.0 -len/size);//画圆
        float sinval = sin(PI*1.*(0.3+0.7*rnd)*iTime+rnd*10.);//加点亮度的变化实现闪烁 
        float period = clamp(pow(pow(sinval,5.),5.),0.,1.);
        float blink =(0.8+0.8*abs(period));
        acc = life*sparksGray*_Color*blink;
    }

    // Output to screen
    fragColor = vec4(acc,1.0);
}
