void mainImage( out vec4 fragColor, in vec2 fragCoord )

{
	vec2 uv = 30. * fragCoord / iResolution.y; 

  	vec3 color = vec3(0, 0.1, 0.3);


    for (int i = 1; i < 24; i++)
    {
       uv +=  sin(uv.yx);
    }



    color += sin(uv.y * 0.4 + 0.5 );



  	fragColor = vec4(color, 1.0);

}
