void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float speed = 2.0;
    float t = speed * iTime;

    // Output to screen
    fragColor = vec4(col,1.0);
    float wave0 = sin(uv.x*300.+iTime*.5);
    float wave1 = sin((uv.x*.90096886+uv.y*.4338837)*300.+t);
    float wave2 = sin((uv.x*.62348980185+uv.y*.78183148)*300.+t);
    float wave3 = sin((uv.x*.22252093395+uv.y*.97492791218)*300.+t);
    float wave4 = sin((uv.y*.97492791218-uv.x*.22252093395)*300.+t);
    float wave5 = sin((uv.y*.78183148246-uv.x*.62348980185)*300.+t);
    float wave6 = sin((uv.y*.43388373911-uv.x*.9009688679)*300.+t);
    float z = 1.0/(1.0+exp(-1.0*(wave0+wave1+wave2+wave3+wave4+wave5+wave6)*2.));
    fragColor = vec4(z,z,z,1.0);
}
