const vec2 LETTER_SIZE = vec2(0.05);

bool isKeyPressed(int ascii) {
    return texelFetch(iChannel2, ivec2(ascii, 1), 0).x > 0.0;
}

float prerandom(in vec2 c) {
    vec2 gen = vec2(114.514, 191.9810);
    return fract(gen.x * sin(gen.y * c.x + c.y));
}

float random(in vec2 c) {
    return prerandom(vec2(
        prerandom(c),
        prerandom(c.yx)
    ));
}

struct Character {
    int code;
    vec2 origin;
    vec2 size;
    vec4 color;
};

Character newChar(in int code, in vec2 origin, in vec2 size, in vec4 color) {
    Character c;
    c.code = code;
    c.origin = origin;
    c.size = size;
    c.color = color;
    return c;
}

bool drawLetter(out vec4 fragColor, in vec2 uv, in Character c) {
    vec2 st = (uv - c.origin) / c.size;
    if (st.x < 0.0 || 1.0 < st.x || st.y < 0.0 || 1.0 < st.y) return false;

    int a = c.code / 16 + 1;
    int b = c.code % 16;
    vec2 base = vec2(float(b), 16.0 - float(a)) / 16.0;

    vec4 color = texture(iChannel0, st / 16.0 + base);
    if (color.x < 0.5) return false;

    fragColor = c.color;
    return true;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float asp = iResolution.x / iResolution.y;
    vec2 uv = fragCoord / iResolution.y;
    if (iFrame == 0) fragColor = vec4(vec3(0.0), 1.0);
    else fragColor = texture(iChannel1, uv / vec2(asp, 1.0));
    for (int key = 65; key < 65 + 26; key++) {
        if (isKeyPressed(key)) {
            float t = iTime + float(key) * sqrt(2.0);
            vec2 o = vec2(t , t + 1.0);
            o = vec2(
                (asp - LETTER_SIZE.x) * random(o),
                (1.0 - LETTER_SIZE.y) * random(o.yx)
            );
            vec3 color = vec3(iTime) + vec3(0.2, 0.3, 0.4);
            color = 0.5 + 0.5 * vec3(random(color.yz), random(color.zx), random(color.xy));
            Character c = newChar(key, o, LETTER_SIZE, vec4(color, 1.0));
            vec4 col;
            if(drawLetter(col, uv, c)) fragColor = col;
        }
    }
}
