#define rnd(x) (sin(9e4*x)*.5+.5)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float split, edgeX1=0., edgeX2=1., edgeY1=0., edgeY2=1., spanX, spanY;
    split = rnd(iMouse.x/10000.);
    for(float i = 0.; i < 10.; i++) {
        spanX = edgeX2 - edgeX1;
        if (spanX < 0.04) break;
        if((uv.x-edgeX1)/spanX < split) {
            edgeX2 = edgeX1 + split*spanX;
            split = rnd(split+10.);
        }
        else {
            edgeX1 = edgeX1 + split*spanX;
            split = rnd(split+20.);
        }

        spanY = edgeY2 - edgeY1;
        if (spanY < 0.02) break;
        if((uv.y-edgeY1)/spanY < split) {
            edgeY2 = edgeY1 + split*spanY;
            split = rnd(split+10.);
        }
        else {
            edgeY1 = edgeY1 + split*spanY;
            split = rnd(split+20.);
        }   
    }
    spanY = edgeY2 - edgeY1;
    spanX = edgeX2 - edgeX1;
    uv.x = (uv.x-edgeX1)/spanX;
    uv.y = (uv.y-edgeY1)/spanY;
    fragColor = vec4(1.5-length(uv*2.-1.))*vec4(
        rnd(split),
        rnd(split+1.),
        rnd(split+2.),
        1.
    );
    fragColor *= step(.5,rnd(floor(length(uv-.5)*50.*split)));
}
