// Adapted from https://www.shadertoy.com/view/ltXGW4

void mainImage( out vec4 c, vec2 p )
{
    vec2 uv = p/iResolution.y + vec2(.2,.5);
    c.w = length(p = iTime*uv - iTime);
    vec2 samp = vec2(atan(p.y,p.x)+.1*iTime, .9/c.w+10.*pow(sin(iTime/10.),6.));
    vec4 c1 = texture( iChannel0, samp)*c.w;
    vec4 c3 = texture( iChannel1, samp)*c.w;
    vec4 c2 = vec4(.03,0.003,0.03,0.)/c.w;
    c = (.7*c1 + .7*c2 + c1*.6*cos(iTime*1.3) + c3*.6*sin(iTime))/c.w;//length(c1+c2) ;//+.1*abs(cos(iTime));
}
