mat2 Rot(float a) {
	float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float aa = 1.5 / iResolution.y;
    uv = vec2( length(uv), atan(uv.y, uv.x)*.1 );
    uv *= .6;
    uv += vec2( length(uv), atan(uv.y, uv.x)*.1 );
    uv *= Rot(iTime*.2);
    uv.y = abs(uv.y);
    vec3 col = vec3(length(uv)*mod(uv.x, uv.y))*4.;
    col = clamp(col, 0., 1.);
    col.b += dot(uv.x*.4, uv.y*5.);
    uv *= Rot(iTime*.2);
    col.r += dot(2., uv.y);

    uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    col *= smoothstep(.4, .4-aa, length(uv));
    col *= 1.4;

    fragColor = vec4(col,1.0);
}
