void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float cellsize = 32.0;
    vec2 uv = fragCoord/iResolution.xy;

    vec2 divpos = floor(fragCoord / cellsize);
    vec2 modpos = mod(fragCoord, cellsize) - (cellsize * 0.5);

    float rot = iTime*4.0 + sin(divpos.x*0.5) + sin(divpos.y*0.5) + divpos.x*0.4;
    vec2 offset = vec2(sin(rot)*10.0, cos(rot)*10.0);
    modpos += offset;

    float c = 1.0 - sqrt(modpos.x*modpos.x + modpos.y*modpos.y) * 0.5;

    // Output to screen
    fragColor = vec4(c,c,c,1.0);
}
