vec3 getBackgroundColor() {
    return vec3(0., 1., 1.);
}

bool sdfSquare(vec2 uv, float size, vec2 offset) {
    float x = uv.x - offset.x;
    float y = uv.y - offset.y;

    float d = max(abs(x), abs(y)) - size;

    return d <= 0.;
}

bool sdfCircle(vec2 uv, float r, vec2 offset) {
    float x = uv.x - offset.x;
    float y = uv.y - offset.y;

    float d = length(vec2(x, y)) - r;

    return d <= 0.;
}

bool sdfHeart(vec2 uv, float size, vec2 offset) {
    float x = uv.x - offset.x;
    float y = uv.y - offset.y;
    float xx = x * x;
    float yy = y * y;
    float yyy = yy * y;
    float group = xx + yy - size;
    float d = group * group * group - xx * yyy;
    vec3 col;

    return d <= 0.;
}

vec3 colorShape(bool inShape, vec3 newCol, vec3 currentCol) {
    return inShape ? newCol : currentCol;
}

vec3 drawScene(vec2 uv) {
    vec3 col = getBackgroundColor();

    col = colorShape(sdfHeart(uv, 0.04, vec2(-0.5, 0.)), vec3(1., 0., 0.), col);
    col = colorShape(sdfSquare(uv, 0.1, vec2(0., 0.)), vec3(0., 1., 0.), col);
    col = colorShape(sdfCircle(uv, 0.1, vec2(0.3, 0.)), vec3(0., 0., 1.), col);
    col = colorShape(sdfSquare(uv, 0.1, vec2(0.6, 0.)), vec3(1., 1., 0.), col);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; // <0, 1>
    uv -= 0.5; // <-0.5,0.5>
    uv.x *= iResolution.x/iResolution.y; // fix aspect ratio


    vec3 col = drawScene(uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}
