float height(vec2 uv){
    vec2 dir = vec2(.5) - uv;
    return mod(atan(dir.y, dir.x)+iTime, 6.2838)/6.2838;
}

vec3 normal(vec2 uv){
    vec2 eps = vec2(.1, 0);
    return (vec3(
        (vec2(
        	height(uv + eps.xy),
            height(uv + eps.yx)
            ) - height(uv))

        ,.8));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

   	vec3 color = vec3(0);

    //color += height(uv);
    //vec3 n = normal(uv);
    //color += dot(n, normalize(vec3(1)));

    color += .5+.5*normal(uv);

    fragColor = vec4(color,1.0);
}
