float elec(vec2 st, float i){
    if(fract(sin(i*100.)+iTime*0.7)> 0.5) return 0.;        
    float ce = 0.;	// ce - color electricity
    st.s += sin(st.t*1.5+iTime+i);
    st.t -= iTime/2.;
    st.s -= iTime/5.+i*2.;
    if(cos(st.s) > 0.){
        float t = st.t*2.;
        float m = min(fract(t), fract(1.-t))-0.25;
        float b = 0.25;
        float w = 0.0;
        float v = sin(st.s)*1.5-m;
        ce = 1.-smoothstep(w , w+b, v);
        ce *= 1.-smoothstep(-w , -w-b, v);
    }
    return ce;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
	uv *= 2.;
    // get polar coordinates
    vec2 st = vec2(
        atan(uv.x, uv.y),
        length(uv)
    );
	// draw eage circle
    float r = 0.98;
    float w = 0.005;
    float b = fwidth(st.t);
    float cc = smoothstep(r-w-b, r-w, st.t); // cc - circle color
    cc *= 1.-smoothstep(r+w , r+w+b, st.t);
    float ce = 0.;       // ce - color electricity
    if(st.t < r-w){
        for( float i = 0.; i < 13.; i += 1.){
			ce += elec(st, i);
        }
    }
    cc += ce;
    fragColor = vec4(0.1, 0.1, cc*0.7, 1.);
}
