void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    vec2 uv = (fragCoord-.5*iResolution.xy)/min(iResolution.x, iResolution.y);
    uv -= vec2(.2, .0);  // translation
    float d = 1.3;
    mat2 rot = mat2(cos(d), -sin(d), sin(d), cos(d)); // rotation
    uv *= rot;

    uv /= 1.5; // scale

    uv /= vec2(max(1.,iResolution.x/iResolution.y), max(1., iResolution.y/iResolution.x));
    uv = uv + .5;

    fragColor = texture(iChannel0, uv);
}
