void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 col1 = vec4(.5,.5,0,0); // color of line
    vec4 col2 = vec4(0,.5,.25,0); // bkg color
    float width = 3.0;
    float scale = 20.0;
    vec2 diff = fract((fragCoord)/scale)*scale; //pretty much just mod10
    float det = max(step(diff.x,width) , step(diff.y,width)); //+1 per line im within width of
    //det -= step(1.1 , det); //eliminate bright spots at crossings
    fragColor =  mix(col2,col1,det); //col1*det + col2*(1.0-det); //set color


}
