void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;  
    vec2 u = uv;
    u *= 1.;
    float t = iTime * 7.3;
    float e = u.y - 0.1;
    e += sin(u.x * 10.0 + t) * .01;
    float f = pow(u.x - 0.5,2.0)*3.0;
    e+=sin(f*2.0+t)*0.1;
    float k = smoothstep(e,f,f*1.05); 
    float m = step(f,e);
    vec3 a = vec3(1.0,0.9,10.0);
    vec3 b = vec3(1.0,0.2,0.3);
    vec3 c = mix(a,b,m / k);
    //c.rg -= k;
    fragColor = vec4(c,1.0);
}
