float hash1(float i )
{
    return fract(sin(i*123.456)*456.123);
}
vec2 hash2(float i)
{
    float x = hash1(i);
    float y = hash1(x);
    return vec2(x,y);
}
vec2 hash2(vec2 uv)
{
    float d = fract(dot(uv,vec2(123.45,456.12)));
    return hash2(d);
}
float hash1(vec2 uv)
{
    float d = fract(dot(uv,vec2(213.45,546.12)));
    return hash1(d);
}

vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}
vec3 rgb2hsb( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}
vec3 hsbmodify(vec3 base,float hue,float saturate,float value)
{
    vec3 hsb = rgb2hsb(base);
    hsb+= vec3(hue,saturate,value);
    hsb.yz = clamp(hsb.yz,vec2(0.),vec2(1));
    return hsb2rgb(hsb);
}

#define smix(a,b,f) sqrt(mix(a*a,b*b,f))
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
	float asp = iResolution.x / iResolution.y;
    const float N = 100.;
    float hue = 0.63;

    vec3 maincolor = hsb2rgb(vec3(hue,.3,.95));
    vec3 col = maincolor;
    const float hue_range = 0.05;
    const float value_range  = 0.2;
    const float sat_range = 0.5;
    for(float i =0.;i< 1. ;i+= 1./N)
    {
        vec2 rp = hash2(i+1.41);
        rp.x *= asp;

        float d = length(uv-rp);
        float r = mix(0.01,0.1,hash1(i+1.1));

        //vec3 randcol = vec3(hash1(i+11.3),hash1(i+1.5),hash1(i+222.1));
        //randcol = mix(randcol, maincolor,0.8);
        float hue = hash1(i+12.1)* 2.* hue_range -hue_range;
        float sat = hash1(i+183.)*2.*sat_range-sat_range;
        float value = hash1(i+541.)*2.*value_range-value_range;
        vec3 randcol = hsbmodify(maincolor,hue,sat,value);
        float f = smoothstep(r,r*0.97,d);
        col = smix(col,randcol,f);
    }
    fragColor = vec4(col,1.0);
}
