// object transformation
vec3 rotate_x(vec3 p, float phi)
{
    float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{
	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{
	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}
///------------------------------------

mat2 rot2D(float a)
{
    float c=cos(a);
    float s=sin(a);
    return mat2(c,-s,s,c);
}


vec2 mapMat(vec3 p)
{
    vec3 q = p;
    p = vec3(mod(p.x, 5.0) - 2.5, p.y, mod(p.z, 5.0) - 2.5);
    p -= vec3(0.0, 0.0, 0.0);
    float qpi = 3.141592 / 4.0;
    float sub = 10000.0;

    for(float i = 0.0; i < 6.0; i++)
    {
        float x = 0.2 * cos(i * qpi);
        float z = 0.2 * sin(i * qpi);
        vec3 transp = p - vec3(x, 0.0, z);
        vec3 a = vec3(x, 1.2, z);
        vec3 b = vec3(x, -1.0, z);
        sub = min(sub, sdCapsule(transp, a, b, 0.1));
    }


    float ttorus = sdTorus(p - vec3(0.0, -1.5, 0.0), vec2(0.22));
    float btorus = sdTorus(p - vec3(0.0, 1.5, 0.0), vec2(0.25));
    float u = min(btorus, ttorus);
    vec2 column = vec2(min(u, max(-sub, length(p.xz) - 0.36)), 2.2);
    return column;
}

vec2 shape(vec3 p, float a) 
{
	float s = sdSphere(abs(p) - vec3(2, 0, 0),  0.5);

    vec2 mm1= mapMat( p);
    float b=mm1.x;

    float b1 = sdBox(p, vec3(1, 1.0 * a, 0.5));
    float s2 = sdSphere(abs(p) - vec3(0, 1., 0),  0.5);

	vec2 res=vec2(9999.9,-1.0);

    res=opU2(res, vec2(s,7.0));
    res=opU2(res, vec2(b,2.0));
    res=opU2(res, vec2(b1,6.0));
    res=opU2(res, vec2(s2,4.0));


	return res;
}

vec2 de(vec3 p) 
{	

	vec4 q = vec4(p*0.1, 1.0);
	vec4 sq = q;

	for(int i = 0; i < 7; i++) {
		q.xyz = abs(q.xyz) - vec3(1.7, 0.1, 0.8);
		q.xz *= rot2D(0.785*2.0 + float(i)*0.785);

		q *= 1.4;

		sq = q;
		sq.xz += 1.2;

	}

	q.xyz = abs(q.xyz) - vec3(0, 4, 0);
	vec2 v = shape(q.xyz, 0.0)/(q.w*0.1);

	sq.xyz = abs(sq.xyz) - vec3(0, 4, 0);
	vec2 h = shape(sq.xyz, 2.0)/(sq.w*0.1);



    vec2 res=vec2(9999.9,-1.0);
    res=opU2(res, v );
    res=opU2(res, h );
    res=opU2(res, vec2(p.y + 0.5, 5.0));

    return res;
}


///------------------------------------
vec3 GetDist(vec3 p  ) 
{	
	vec3 res;
    vec3 pp=p;
    res = vec3(9999.0, -1.0,-1.0);


	float planeDist1 = p.y+0.0;  //piso inf

    res =opU3(res, vec3(planeDist1,-1.0,5.0)); //inf

    p.y=p.y-5.0;
    vec2 d1= de(p);
    res =opU3(res, vec3(d1.x,d1.y,MATERIAL_NO)); 


    //res =opU3(res, vec3(1.0,0.0,MATERIAL_NO)); 
    //return (dist, id_color, id_material)
    return res;
}




vec3 LightShadingV2(vec3 normal,vec3 p, vec3 ro,vec3 rd, vec3 plight_pos,vec3 color)
{   
    vec3 final = vec3( 0.0 );
    //const float shininess = 64.0;
    float shininess = 64.;
    vec3 lig = normalize(plight_pos);
	vec3 l = normalize( p-plight_pos);
    vec3 v = normalize( p-ro);
    vec3 h = normalize(v + l);


    float diff = dot(normal, l);
    vec3 vl = normalize( plight_pos - p );
    vec3 ref_v = reflect( v, normal );
    float diffuse  = max( 0.0, dot( vl, normal ) );
    float specular = max( 0.0, dot( vl, ref_v ) );

		specular = pow( specular, shininess );
		final += color * ( diffuse + specular);

    return  final;


}   


//------------------------------------------------
vec3 GetNormal(vec3 p)
{
	float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);
    return normalize(n);
}


float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;
    for(int i=0; i<MAX_STEPS; i++) 
    {
    	p = ro + rd*dO;
        dS = GetDist(p);

        if(dO>MAX_DIST || abs(dS.x)<MIN_DIST) break;
        dO += dS.x;
        marchCount++;

    }

    mObj.dist = dO;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    return dO;
}

//---------------------------------------------------
float GetShadow(vec3 p, vec3 plig)
{
    vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}
