 	light_pos1= vec3(10.0, 6.0, 10.0 );  light_color1 = vec3( 1.0 ,0.0,0.0);
 	light_pos2= vec3( -5.0, 6.0, -5.0 ); light_color2 = vec3( 1.0, 1.0, 1.0 );


    vec3 ro=vec3(2.0,13.0,-25.0+10.0*sin(iTime*0.25));

      vec3 rd=normalize(vec3(uv,1.0));


    light_pos1+=ro;
