void mainImage( out vec4 o, vec2 FC ) {
    o = texelFetch(iChannel1,ivec2(FC),0);
    o.rgb = pow(o.rgb,vec3(.3));
    //o.rgb = pow(vec3(o.a),vec3(1.));
}

vec2 turn(vec2 uv, vec2 p){
    vec2 sensorL = uv+(rot(-LOOKUP_ANGLE)*p)*LOOKUP_DIST; // location of sensor A
    vec2 sensorC = uv+p*LOOKUP_DIST;
    vec2 sensorR = uv+(rot(LOOKUP_ANGLE)*p)*LOOKUP_DIST;

    float senseL = texture(iChannel1, sensorL).a;
    float senseC = texture(iChannel1, sensorC).a;
    float senseR = texture(iChannel1, sensorR).a;

    if (senseC < senseL && senseC < senseR){
      if(rnd(p.x)<.5)
          p*=rot(LOOKUP_ANGLE);
      else
          p*=rot(-LOOKUP_ANGLE);
    }
    else if(senseL < senseR){
      p *= rot(-LOOKUP_ANGLE);
    }
    else if(senseL > senseR){
      p *= rot(LOOKUP_ANGLE);
    }
    return p;
}


void mainImage( out vec4 o, in vec2 FC ){
    vec2 uv=FC.xy/iResolution.xy,p_g,v_g;
    vec4 n,g;
    o-=o;
    if(mod(iTime, 20000.)<=4.1){if(length(mod(vec2(FC.xy-.5), 10.)) == 0. && length(uv-.5) < .5)o+=vec4(.5,.5,-(uv+.5)*.5+.5);return;}
    for(float i=-C;i<=C;i++){
      for(float j=-C;j<=C;j++){
        vec2 ij=vec2(i,j);
        vec2 uv_g = fract(uv+ij/iResolution.xy);
        g=texture(iChannel0,uv_g);
        if(length(g)==0.)continue;
        p_g=g.rg+ij;
        v_g=g.ba;
        if(p_g+v_g==fract(p_g+v_g)){
          p_g = fract(p_g+v_g);
          v_g=turn(uv_g,v_g);

          //v_g+=vec2(.0,.2)*rot(atan(uv.y-.5,uv.x-.5));

          vec2 g_ij=ij;
          float sig = round(rnd(length(uv)))*2.-1.;
          for(float di=-R*sig;abs(di)<=R;di+=sig){
            for(float dj=-R*sig;abs(dj)<=R;dj+=sig){
              vec2 n_ij=vec2(g_ij.x+di,g_ij.y+dj);
              if(length(n_ij)>0.&&length(n_ij)<R){
                vec2 uv_n=fract(uv+n_ij/iResolution.xy);
                n=texture(iChannel0,uv_n);
                if(length(n)==0.)continue;
                vec2 p_n = n.rg+n_ij;
                float dist=length(p_g-p_n);
                v_g+=.5*normalize(p_g-p_n)/(dist*dist);
              }
            }
          }

          v_g=C*normalize(v_g);
          o=vec4(p_g,v_g);
          break;
        }
      }
    }

    if(length(o)==0.){
        vec2 ij=vec2(.0,2.)*rot(3.14159265*.5*float(iFrame%4));
        vec4 n=texture(iChannel0,fract(uv+ij/iResolution.xy));
        if(length(n)==0.)
            return;
        if(rnd(length(uv)+iTime)<.08)
            o=n;
    }
}

void mainImage( out vec4 o, in vec2 FC ){
    if(iFrame==0){o-=o;return;}
    vec2 uv=FC.xy/iResolution.xy;
    vec4 cell=texture(iChannel0,uv);
    if(length(cell)>0.){o=vec4(cell.zw/C*.5+.5,1.,1.);return;}
    o=(texture(iChannel1,uv,.1))*.99;
}
