void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-0.5; // [-0.5, 0.5]
    float mipmap_level = 0.;
    float mix_alpha = 0.;


    if(abs(uv.x) > abs(uv.y) ) {
        float l = abs(uv.x);
        if(abs(uv.x) > 0.25) {
            float l_new = (l-0.25)/0.25*0.05 + 0.45;
            uv *= l_new/l;
            mipmap_level = 4.0; //mix_alpha = (l-0.25)/0.25;
        }else {
            float l_new = l/0.5*0.45*2.;
            uv *= l_new/l;
        }
    }  


    if(abs(uv.y) > abs(uv.x) ) {
        float l = abs(uv.y);
        if(abs(uv.y) > 0.25) {
            float l_new = (l-0.25)/0.25*0.05 + 0.45;
            uv *= l_new/l;
            mipmap_level = 4.0 ; //mix_alpha = (l-0.25)/0.25;
        }else {
            float l_new = l/0.5*0.45*2.;
            uv *= l_new/l;
        } 
    }

    // Output to screen
    fragColor = mix(texture(iChannel0, uv+0.5, mipmap_level), vec4(1., 1., 1., 1.), mix_alpha) ;
}
