vec3 hsv( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	rgb = rgb*rgb*(3.0-2.0*rgb);
	return c.z * mix( vec3(1.0), rgb, c.y);}
//smooth hsv from iq https://www.shadertoy.com/view/MsS3Wc

vec4 qmult(vec4 a,vec4 b){return vec4(a.x*b.x-a.y*b.y-a.z*b.z-a.w*b.w,a.x*b.y+a.y*b.x+a.z*b.w-a.w*b.z,a.x*b.z-a.y*b.w+a.z*b.x+a.w*b.y,a.x*b.w+a.y*b.z-a.z*b.y+a.w*b.x);}

vec4 qexp(vec4 a){
float l=length(a.yzw);
float ls=sin(l)/l;
return exp(a.x)*vec4(cos(l),a.y*ls,a.z*ls,a.w*ls);}

vec4 iter(vec4 a){
float U=24.5+.503*sin(T*.75);
return qmult(qexp(a),1.5*vec4(.5*sin(U*.25),sin(U/6.),sin(U*.1),sin(U/14.)));}

