float f(float x,float k1,float k2)
{
    float a = 1.0-pow(abs(x*k1)-1.0,2.0);
    return sqrt(a)/k2;
}
float h(float x,float k1,float k2)
{
    float h = sqrt(1.0-0.5*abs(x*k1));
    return -2.0*h/k2;
}

float heart(vec2 uv,float offestX,float offestY,float blur)
{
   float x =uv.x;
   float y =uv.y-0.1;
   float f = f(x,5.0+offestX,4.0+offestY);
   float h = h(x,5.0+offestX,4.0+offestY);
   float s =smoothstep(f+blur,f,y)*smoothstep(h-blur,h,y);
   return s;
}
float AnimationHeart(vec2 uv,float blur,float time)
{
    float offestX = 1.0*abs(sin(time));
    float offestY = offestX/4.0;
    return heart(uv,offestX,offestY,blur);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x = uv.x*iResolution.x/iResolution.y;

    // Output to screen
    fragColor = vec4(vec3(AnimationHeart(uv,0.02,iTime)),1.0);

}
