float guitarStr(vec2 uv, float pl, float pu, float sl, float su, float y, float f)
{

    float dl = pl + abs(sin(uv.x * 151.0)) * sl;
    float du = pu + abs(sin(uv.x * 152.0)) * su;

    float ff = f * 0.01 + y;

    return smoothstep(uv.y - dl, uv.y, ff) - smoothstep(uv.y, uv.y + du, ff);


}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    float aspct = (iResolution.x / iResolution.y); 
    uv.x *= aspct;

    uv *= 0.75;
    uv.y -= 0.085;

    float fofx = 0.0;

    float str0 = texture(iChannel0, vec2(uv.x / (aspct * 100.0) + 0.50, 0.75)).x;
    float str1 = texture(iChannel0, vec2(uv.x / (aspct * 50.0) + 0.50, 0.75)).x;

    fofx += guitarStr(uv, 0.003, 0.0031,   0.0,   0.0,   0.4, str0 * 2.0);
    fofx += guitarStr(uv, 0.007, 0.0071,    0.0,   0.0,   0.2, str0 * 4.0);
    fofx += guitarStr(uv, 0.013, 0.012,    0.0,   0.0,   0.0,str0 * 5.0);
    fofx += guitarStr(uv, 0.007, 0.008,    0.009, 0.03, -0.2, str0 * 9.0);
    fofx += guitarStr(uv, 0.007, 0.008,    0.01,  0.04, -0.4, str1 * 8.0);
    fofx += guitarStr(uv, 0.007, 0.008,    0.02,  0.048, -0.6, str0 * 11.0);

    fragColor = vec4(vec3(fofx), 1.0);
}
