// fork of "Texture curvature (Laplacian)" https://shadertoy.com/view/ls2BDt

void mainImage(out vec4 O, vec2 U) {
    O = T(U);
}

void mainImage(out vec4 O, vec2 U) {
    O = T(U);

    O += .1 * ( // Laplacian
                  ( T(U+vec2(1,0)) + T(U+vec2(-1,0)) + T(U+vec2(0,1)) + T(U+vec2(0,-1)) ) /4. 
                  - O 
               );

    if (iFrame<10) O = texture(iChannel1,U/iResolution.xy);
}
