// Modeling - 2023.02.06
// Eric Galin

// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
  float sa=sin(a);float ca=cos(a);
  return mat3(ca,sa,0.,-sa,ca,0.,0.,0.,1.);
}

// Compute the ray
//      m : Mouse position
//      p : Pixel
// ro, rd : Ray origin and direction
void Ray(vec2 m,vec2 p,out vec3 ro,out vec3 rd)
{
  float a=3.*3.14*m.x;
  float le=3.5;

  // Origin
  ro=vec3(37.,0.,15.);
  ro*=Rz(a);

  // Target point
  vec3 ta=vec3(0.,0.,1.);

  // Orthonormal frame
  vec3 w=normalize(ta-ro);
  vec3 u=normalize(cross(w,vec3(0.,0.,1.)));
  vec3 v=normalize(cross(u,w));
  rd=normalize(p.x*u+p.y*v+le*w);
}

// Primitives

// Sphere
// p : point
// c : center of skeleton
// r : radius
float Sphere(vec3 p,vec3 c,float r)
{
  return length(p-c)-r;
}


// Operators

// Union
// a,b : field function of left and right sub-trees
float Union(float a,float b)
{
  return min(a,b);

}
// intersection
float Intersection(float a,float b)
{
  return max(a,b);
  }

// Plan
// p : point
// n : norme du paln
// d : distance
float DemiPlan(vec3 p,vec3 n, vec3 c)
{
  n= normalize (n);
  return dot(p-c,n);
}




// capsule

   float capsule(vec3 p, vec3 a, vec3 b, float r)
{
    vec3 u = normalize(b-a); // Normalisation du vecteur direction de la capsule
    float l = dot(p-a, u); // Calcul l 

    float d;

    if (l < 0.)
    {

        d = length(p-a)-r; 
    }
    else if (l > length(b-a))
    {

        d = length(p-b)-r; 
    }
    else
    {

        d = sqrt(length(p-a) * length(p-a)-l*l)-r; 
    }
    return d; 
}
// Tore
// p : point
// c : centre
// r1 : rayon section centrale 
// r2 : rayon cercle
float Tore(vec3 p, vec3 c, float r1, float r2)
{
    p=p-c;
  vec2 q = vec2(length(p.xz) - r1, p.y);
  return length(q) - r2;
}

// DemiCercle
// p : point
// c : centre du cercle
// n : normale du plan du cercle
// r : rayon du cercle
// Demi-cercle
float DemiCercle(vec3 p, vec3 n, vec3 c, float r)
{
    // Calcul de la distance du point au plan
    float distancePlan = DemiPlan(p, n, c);

    // Calcul de la distance du point à la sphère
    float distanceSphere = Sphere(p, c, r);

    // Intersection entre le demi-plan et la sphère
    float intersection = Intersection(distancePlan, distanceSphere);

    // Retourner l'intersection pour former le demi-cercle
    return intersection;
}


float boite(vec3 p, vec3 c, vec3 size)
{   
    p = p - c;
    float halfWidth = size.x / 2.0;
    float halfHeight = size.y / 2.0;
    float halfDepth = size.z / 2.0;

    float boxX = abs(p.x) - halfWidth;
    float boxY = abs(p.y) - halfHeight;
    float boxZ = abs(p.z) - halfDepth;

    float box = max(max(boxX, boxY), boxZ);

    return box;
}


float Difference(float a, float b)
{
    return max(a, -b);
}
// Fonction pour dessiner le robot
float DessinerRobot(vec3 p)
{
    // Tête du robot (sphère)
    float tete = Sphere(p, vec3(0.0, 5.0, 0.0), 1.5); // Position de la tête ajustée en haut

    // Corps du robot (boîte)
    float corps = boite(p, vec3(0.0, 3.0, -4.0), vec3(2.5, 2.5, 6.0)); // Position du corps ajustée en haut

    // Bras du robot (boîtes) inclinés et de taille plus importante
    float brasGauche = boite(p, vec3(1.8, 4.0, -1.0), vec3(1.0, 1.0, 4.5)); // Position et taille des bras ajustées
    float brasDroit = boite(p, vec3(-1.8, 4.0, -1.0), vec3(1.0, 1.0, 4.5)); // Position et taille des bras ajustées

    // Jambes du robot (boîtes)
    float jambeGauche = boite(p, vec3(1.2, 2.5, -6.0), vec3(0.8, 0.8, 6.0)); // Taille des jambes augmentée
    float jambeDroite = boite(p, vec3(-1.2, 2.5, -6.0), vec3(0.8, 0.8, 6.0)); // Taille des jambes augmentée

    // Yeux du robot (sphères)
    float oeilGauche = Sphere(p, vec3(0.7, 6.5, 1.5), 0.6); // Position et taille des yeux ajustées
    float oeilDroit = Sphere(p, vec3(-0.7, 6.5, 1.5), 0.6); // Position et taille des yeux ajustées

    // Combinaison de toutes les parties pour former le robot
    float robot = Union(tete, Union(corps, Union(brasGauche, Union(brasDroit, Union(jambeGauche, Union(jambeDroite, Union(oeilGauche, oeilDroit)))))));

    return robot;
}




float object(vec3 p)
{
     // Appel de la fonction DessinerRobot pour dessiner le robot
    return DessinerRobot(p);
}


// Analysis of the scalar field

const int Steps=200;// Number of steps
const float Epsilon=.01;// Marching epsilon

// Object normal
// p : point
vec3 ObjectNormal(vec3 p)
{
  const float eps=.001;
  vec3 n;
  float v=object(p);
  n.x=object(vec3(p.x+eps,p.y,p.z))-v;
  n.y=object(vec3(p.x,p.y+eps,p.z))-v;
  n.z=object(vec3(p.x,p.y,p.z+eps))-v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance
// h : hit
// s : Number of steps
float SphereTrace(vec3 o,vec3 u,float e,out bool h,out int s)
{
  h=false;

  // Start at the origin
  float t=0.;

  for(int i=0;i<Steps;i++)
  {
    s=i;
    vec3 p=o+t*u;
    float v=object(p);
    // Hit object
    if(v<0.)
    {
      h=true;
      break;
    }
    // Move along ray
    t+=max(Epsilon,v);
    // Escape marched too far away
    if(t>e)
    {
      break;
    }
  }
  return t;
}

// Lighting

// Background color
// d : Ray direction
vec3 background(vec3 d)
{
  return mix(vec3(.45,.55,.99),vec3(.65,.69,.99),d.z*.5+.5);
}

// Shadowing
// p : Point
// n : Normal
// l : Light direction
float Shadow(vec3 p,vec3 n,vec3 l)
{
  bool h;
  int s;
  float t=SphereTrace(p+Epsilon*n,l,100.,h,s);
  if(!h)
  {
    return 1.;
  }
  return 0.;
}

// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction
vec3 Shade(vec3 p,vec3 n,vec3 e)
{
  // Point light
  const vec3 lp=vec3(5.,10.,25.);

  // Light direction to point light
  vec3 l=normalize(lp-p);

  // Ambient color
  vec3 ambient=.25+.25*background(n);

  // Shadow computation
  float shadow=Shadow(p,n,l);

  // Phong diffuse
  vec3 diffuse=.35*clamp(dot(n,l),0.,1.)*vec3(1.,1.,1.);

  // Specular
  vec3 r=reflect(e,n);
  vec3 specular=.15*pow(clamp(dot(r,l),0.,1.),35.)*vec3(1.,1.,1.);
  vec3 c=ambient+shadow*(diffuse+specular);
  return c;
}

// Shading according to the number of steps in sphere tracing
// n : Number of steps
vec3 ShadeSteps(int n)
{
  float t=float(n)/(float(Steps-1));
  return.5+mix(vec3(.05,.05,.5),vec3(.65,.39,.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel,out bool pip)
{
  // Pixel coordinates
  vec2 p=(-iResolution.xy+2.*pixel)/iResolution.y;
  if(pip==true)
  {
    const float fraction=1./4.;
    // Recompute pixel coordinates in sub-picture
    if((pixel.x<iResolution.x*fraction)&&(pixel.y<iResolution.y*fraction))
    {
      p=(-iResolution.xy*fraction+2.*pixel)/(iResolution.y*fraction);
      pip=true;
    }
    else
    {
      pip=false;
    }
  }
  return p;
}

// Image
void mainImage(out vec4 color,in vec2 pxy)
{
  // Picture in picture on
  bool pip=true;

  // Pixel
  vec2 pixel=Pip(pxy,pip);

  // Mouse
  vec2 m=iMouse.xy/iResolution.xy;

  // Camera
  vec3 ro,rd;
  Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t=SphereTrace(ro,rd,100.,hit,s);

  // Shade background
  vec3 rgb=background(rd);

  if(hit)
  {
    // Positionhttps://www.shadertoy.com/img/themes/classic/play.png
    vec3 p=ro+t*rd;

    // Compute normal
    vec3 n=ObjectNormal(p);

    // Shade object with light
    rgb=Shade(p,n,rd);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if(pip==true)
  {
    rgb=ShadeSteps(s);
  }

  color=vec4(rgb,1.);
}
