// Flamished Fractalited Formings
// leon denise 2023-12-24

// render pass

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 map = texture(iChannel0, uv);

    // dim (clean the seams)
    map.r -= 0.1;

    // blured normal
    #define T(u) textureLod(iChannel0, uv+u, 4.).r
    vec3 ep = vec3(10./iChannelResolution[0].xy,0);
    vec3 normal = normalize(vec3(T(-ep.xz)-T(ep.xz), T(-ep.zy)-T(ep.zy), map.r));

    // lighting
    float light = dot(normal, normalize(vec3(0,1,1)))*.5+.5;
    vec3 color = vec3(light*light);

    // stencil
    color *= smoothstep(.0,.01,map.r);

    fragColor = vec4(color,1.0);
}

// Flamished Fractalited Formings
// leon denise 2023-12-24

// feedback displace pass

float gyroid (vec3 seed) { return dot(sin(seed),cos(seed.yzx)); }

// crazy noise
float fbm (vec2 pos)
{
    vec3 p = vec3(pos, iTime*.1);
    float result = 0., a = .5;
    for (int i = 0; i < 3; ++i, a /= 2.) {
        result += abs(gyroid(p/a)*a);
    }
    result = sin(result*6.283+iTime*.5-length(pos));
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (2.*fragCoord-iResolution.xy)/iResolution.y;

    // curl noise
    vec2 e = vec2(1./iResolution.y,0);
    vec2 curl = vec2(fbm(p+e.xy)-fbm(p-e.xy), fbm(p+e.yx)-fbm(p-e.yx)) / (2.*e.x);
    curl = vec2(curl.y, -curl.x);

    // spawn shape
    float dist = abs(length(p)-.5);
    float mask = smoothstep(.01, 0., dist);

    // displace
    curl *= 0.005;
    vec4 frame = texture(iChannel0, uv + curl);

    // feedback
    float FFT = texture(iChannel1, vec2(0.07, 0.25)).r * 0.005;
    mask = max(mask, frame.r - FFT);

    fragColor = vec4(mask, curl, 1);
}
