void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mat4 dither;
    dither[0] = vec4(0.0, 12.0, 3.0, 15.0);
    dither[1] = vec4(8.0, 4.0, 11.0, 7.0);
    dither[2] = vec4(2.0, 14.0, 1.0, 13.0);
    dither[3] = vec4(10.0, 6.0, 9.0, 5.0);
    dither /= 16.0;

    fragCoord = fragCoord / 4.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = floor(fragCoord)/iResolution.xy;

    // Time varying pixel color
    vec3 tex_col = texture(iChannel0, uv * 4.0).xyz;

    float desaturate = dot(tex_col, vec3(0.21, 0.72, 0.07));
    int dither_x = int(mod(fragCoord.x, 4.0));
    int dither_y = int(mod(fragCoord.y, 4.0));
    float dithered = desaturate + (dither[dither_x][dither_y] * 0.125);
    float quantize = 0.0;
    if (iMouse.x / (fragCoord.x * 4.0) > uv.x * 4.0) {
        quantize = floor(dithered * 8.0) / 8.0;
    } else {
        quantize = floor(desaturate * 8.0) / 8.0;
    }



    vec3 col = vec3(quantize);

    // Output to screen
    fragColor = vec4(col,1.0);
}
