void mainImage(out vec4 o, vec2 q) { // 244ch, @fabrice
    o.xyz = iResolution; 
    float x = 3. * round(length(q += q - o.xy) / o.y 
                 * (30. + 25. * tanh(6. * cos(iTime * .3)))),  
          T = 6.283 / x;                

    o = vec4(
            length(
                fract(
                    vec2(x,
                         floor(
                             mod(
                                 tanh(cos(x * 1.0433 + iTime) * 4.) 
                                     + atan(q.y, q.x), 
                                 6.283 
                             ) / T 
                         ) * T * 3.
                     )      
                 )  - .5 
             ) - .6 
         ) * 7.;
}


