float Circle (vec2 uv, vec2 p, float r, float blur) {
    float d = length(uv - p);
    float c = smoothstep(r, r - blur, d);
    return c;
}

float Smiley (vec2 uv, vec2 p, float size) {

    uv -= p;
    uv /= size;

    float mask = Circle(uv, vec2(.0), .4, .05);
    mask -= Circle(uv, vec2(.13, .2), .07, .01);
    mask -= Circle(uv, vec2(-.13, .2), .07, .01);

    float mouth = Circle(uv, vec2(0., 0.), .3, .02);
    mouth -= Circle(uv, vec2(0., 0.1), .3, .02);

    mask -= mouth;

    return mask;
}

float Band (float t, float start, float end, float blur) {
    float step1 = smoothstep(start - blur, start + blur, t);
    float step2 = smoothstep(end + blur, end - blur, t);

    return step1 * step2;
}

float Rect (vec2 uv, float left, float right, float top, float bottom, float blur) {
    float x = Band(uv.x, left, right, blur);
    float y = Band(uv.y, top, bottom, blur);
    return x * y;
}

float remap (float a, float b, float c, float d, float t) {
    return (t - a) / (b - a) * (d - c) + c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float t = iTime;

    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(.0);

    float mask = Smiley(uv, vec2(0.), 1.);

    float x = uv.x;

    float m = sin(t * 6. + x * 8.) * .1;

    float y = uv.y + m;

    float blur = remap(-.5, .5, -.01, .25, x);
    //blur = blur * blur;

    mask = Rect(vec2(x, y), -.5, .5, -.1, .11, blur);

    col = vec3(1., 1., 1.) * mask;
    // col = vec3(mouth);

    fragColor = vec4(col, 1.0);
}
