void Unity_PolarCoordinates(vec2 UV, vec2 Center, float RadialScale, float LengthScale, out vec2 Out)
{
    vec2 delta = UV - Center;
    float radius = length(delta) * 2.0 * RadialScale;
    float angle = atan(delta.x, delta.y) * 1.0/6.28 * LengthScale;
    Out = vec2(radius, angle);

}



void Unity_Rotate_Radians(vec2 UV, vec2 Center, float Rotation, out vec2 Out)
{
    UV -= Center;
    float s = sin(Rotation);
    float c = cos(Rotation);
    mat2 rMatrix = mat2(c, -s, s, c);
    rMatrix *= 0.5;
    rMatrix += 0.5;
    rMatrix = rMatrix * 2.0 - 1.0;
    UV.xy = UV.xy * rMatrix;
    UV += Center;
    Out = UV;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    //fragColor = vec4(col,1.0);

    vec2 polarout = vec2(0,0);

    Unity_Rotate_Radians(uv ,  vec2 (0.5,0.5) , iTime , polarout);

    Unity_PolarCoordinates(polarout, vec2 (0.5,0.5), 1.0, 0.5, polarout);

    float rayosOut = polarout.y * 900.0;

    vec4 color = vec4(0.75,0.75,0.75,1.);

    fragColor = vec4(color * sin(rayosOut));
}
