// Fork of "Collatz Conjecture." by RJVB09. https://shadertoy.com/view/NslBWs
// 2024-05-30 09:44:33

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord.xy + iMouse.xy * 100.;

    int nimsum = int(p.y) ^ int(p.x);

    //int num = int(fragCoord.y * iResolution.y + fragCoord.x);
    //int num = nimsum;
    int num = int(float(nimsum) + iTime * 12.);

    int tries = 0;

    while (num > 1)
    {
        if (num % 2 == 1)
        {
            num = num * 3 + 1;
        }
        else
        {
            num /= 2;
        }
        tries++;
    }


    //vec3 col = vec3(float(tries)/200.0);
    //vec3 col = vec3(float(nimsum % tries == 1 ? 1 : 0));
    vec3 col = vec3(float(nimsum % tries) / float(tries));


    // Output to screen
    fragColor = vec4(col,1.0);
}
