vec3 c(vec2 uv) {
    vec3 b = vec3(0.5, 0.8, 0.9);
    return texture(iChannel0, vec3(uv * 2.0 - 1.0, 1.0)).rgb;
}

vec3 v(vec2 uv) {
    vec2 r = uv * vec2(1, -1) + vec2(0, 1);
    vec2 dst = texture(iChannel1, vec3(uv.x, 1.0/(1.0 - 2.0*uv.y), iTime / 20.0)).xy * 2.0 - 1.0;
    dst *= r.y * 2.0 - 1.0;
    vec3 v = vec3(r, 1.0);
    return c(r + dst * vec2(0.2, 1.0) / 40.0) * (1.5 - v.y) + vec3(0.05, 0.2, 0.25) * v.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = c(uv);
    if (uv.y < 0.5) col = v(uv);
    fragColor = vec4(col,1.0);
}
