void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.xy;
    uv *= vec2(0.75, 1.0);

    float ns = 2.0;
    float nt = iTime * ns;
    nt -= mod(nt, 240.0 / 256.0 / 6.0);
    nt = mod(nt, 240.0 / 256.0);

    float ny = fract(iTime * ns);
    ny -= mod(ny, 0.75);
    ny *= -0.05;

    fragColor = texture(iChannel0, vec2(
        uv.x / 3.0 + 210.0 / 256.0 - nt + 0.035,
        0.5 + uv.y - ny
    ));

    if (abs(uv.x) > 0.25) fragColor.a = 0.0;
    fragColor *= fragColor.a;
}
