float rnd(vec2 uv, float n) {
    return fract(1e4*sin(uv.x*10.0+10.0*pow(1.0+uv.y, 2.5)+n*(2.0+uv.x)*(2.0+uv.y)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.0, 0.0, 0.0);

    for(float i = 0.0; i<200.0;++i) {
        vec2 v = vec2(i,i*3.0);
        float y = rnd(v,2.0);
        float r = 0.1+y*0.4+0.6*rnd(v, 0.0);
        float s = rnd(v, 1.0)*6.28;
        float x = 0.5+0.4*r * sin(s+iTime*(1.0+y*2.0));
        vec2 p = uv-vec2(x,y);
        p.x*=0.3+abs(x-0.5)+1.0-y;
        float l = max(abs(p.x), abs(p.y));
        if(l<0.02){
            l=1.0-l/0.02;
            l/=1.5;
            l=pow(l,2.0);
        } else {
            l=0.0;
        }
        col.x=max(col.x,l);
        col.y=max(col.y, l*(1.0-y));
        col.z=max(col.z,l*rnd(v, 3.0));
    }
    fragColor = vec4(col,1.0);
}
