#define PI 3.1415926538

vec2 cMul(in vec2 z1, in vec2 z2) {
    return  mat2(z1,-z1.y,z1.x) * z2;
}

vec2 cDiv(in vec2 z1, in vec2 z2) {
    return z1 * mat2(z2,-z2.y,z2.x) / dot(z2,z2);
}

vec2 lattes(in vec2 z) {
    vec2 z_2 = cMul(z, z);
    vec2 numerator = z_2 + vec2(1.0, 0.);
    numerator = cMul(numerator, numerator);
    vec2 denom = cMul(vec2(4.0, 0.), cMul(z, z_2 - vec2(1.0, 0.)));
    return cDiv(numerator, denom);
}

vec2 lattes_n(in vec2 z, in int n) {
    vec2 result = z;
    for (int i =0; i < n; i++) {
        result = lattes(result);
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = 6. * (2.*fragCoord-iResolution.xy) / max(iResolution.x, iResolution.y);
    vec2 unit = vec2(1., 0.);
    float factor = sin(iTime * .1);
    p = lattes_n(p, 3); // 2 fixed points, loxodromic
    //p = cDiv(unit, p); // 1 fixed point, parabolic

    // Use these for elliptic/hyperbolic/loxodromic transforms depending on where itime is
    // itime on both means loxodromic
    // just on theta is elliptic
    // just on length is hyerbolic
    float theta = atan(p.y, p.x);
    float len = length(p);
    float x_index = sin( PI* ( theta * 12. / PI - iTime ));
    float y_index = sin( PI* ( log(len) * 4.  - iTime));

    // Use these for parabolic mobious transformations
    //float x_index = sin( PI* ( p.x * 10. + iTime ));
    //float y_index = sin( PI* ( p.y * 10.  ));

    float v = x_index*y_index;


    //Thank you to Fabrice for the anti anliasing tip
    fragColor = vec4( .5 + v/fwidth(v) );
}
