/*
pos either x or y coord for vertical or horizontal lines
thickness > 0.0
hardness < 1.0
*/
void drawLine(inout vec3 col, in vec3 lineColor, in float pos, in float thickness, in float hardness){
    col = mix(col, lineColor, smoothstep(thickness, thickness * hardness, abs(pos)));
}
/*
thickness > 0.0
hardness < 1.0
*/
void drawCircle(inout vec3 col, in vec3 lineColor, in vec2 pos, in float radius, in float thickness, in float hardness){
    col = mix(col, lineColor, smoothstep(thickness, thickness * hardness, abs(length(pos) - radius)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord * 3.0 / iResolution.y;
    vec3 col = vec3(0.0);
    drawLine(col, vec3(1.0, 0.0, 0.0), uv.x - 1.1 + cos(iTime), 0.05 + sin(iTime) * 0.02, 0.99);
    drawLine(col, vec3(0.0, 0.0, 1.0), uv.x - 4.2 + sin(iTime), 0.1 + sin(iTime) * 0.04, 0.01);
    drawCircle(col, vec3(0.0, 1.0, 0.0), uv.xy - vec2(2.6, 1.5), 1.0 + 0.2 * sin(iTime), 0.05, 0.1);
    fragColor = vec4(col, 1.0);
}
