// Fork of "better template" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-06-24 01:15:35

#define pi 3.1416*
void mainImage(out vec4 O,vec2 F)
{
    F*=.02;
    O=vec4(fract(length(sin(pi F))));
    if (sin(pi F.y)*sin(pi F.x)>.0) O=O*(sqrt(abs(fract(F.x)-.5))+sqrt(abs(fract(F.y)-.5)));
    else O-=2.*(abs(fract(F.x)-.5)+abs(fract(F.y)-.5));
    O=(O-.6)*20.+.6;
    O=clamp(O,vec4(.0),vec4(1.));
    O=O*vec4(0,.8,1,1)+(1.-O)*vec4(.7,.6,.2,1);
    if (length(fract(F)-.5)<.2) O=vec4(1,.8,0,1);
}
